scripts.updateNginx:
echo "location /_slackcommand {
  proxy_set_header Host \$host;
  proxy_set_header X-Real-IP \$remote_addr;
  proxy_pass http://127.0.0.1:$(node -e "console.log(require('./config/config.js').slackCommandServer.portNumber)");
}" > /app/nginx/slack-bot.conf && systemctl restart nginx
